import { Component } from 'react';
import './App.css';

// COMPONENT
class App extends Component{
  constructor(){
    super();
    this.state={
      monsters: [] //Empty array of values as STATE, then we populate
  };
  }

  // API requests we place here so our components receive the data they need
  componentDidMount(){
    fetch('https://jsonplaceholder.typicode.com/users')
    .then((response) => response.json())
    .then((users)=>
      this.setState(
        ()=>{return{monsters: users}},
        ()=>{console.log(this.state)}
      ))
  }

  render (){
    return(
      //-----------------------------------
        <div className="App">
          {
            this.state.monsters.map((monster)=>{
              return <div key={monster.id}>{monster.name}</div>
            })
          }
        </div>        
      //-----------------------------------
    );
  }
}

export default App
