import { useState, useEffect } from 'react';

import './App.css';
import CardList from './components/card-list/card-list.component';
import SearchBox from './components/search-box/search-box.component';

const App = () => {
	const [searchField, setSearchField] = useState('a'); //[value, setvalue]=useState(initialValue)
	const [monsters, setMonsters] = useState([]);

	console.log('render');

	//({function, effect we want}, [dependencies,state values or props, if they changes I activate the function])
	useEffect(() => {
		console.log('Event fired');
		fetch('https://jsonplaceholder.typicode.com/users')
			.then((response) => response.json())
			.then((users) => setMonsters(users));
	}, []); //Empty dependency --> "use function only once when it mounts"

	const onSearchChange = (event) => {
		const searchFieldString = event.target.value.toLocaleLowerCase();
		setSearchField(searchFieldString);
	};

	const filteredMonsters = monsters.filter((monster) => {
		return monster.name.toLocaleLowerCase().includes(searchField);
	});

	return (
		<div className='App'>
			<h1 className='app-title'>Monsters Rolodex</h1>

			<SearchBox
				onChangeHandler={onSearchChange}
				placeHolder={'search monsters'}
				className={'monsters-search-box'}
			/>
			<CardList monsters={filteredMonsters} />
		</div>
	);
};

