import { Component } from 'react';
import './App.css';

// COMPONENT
class App extends Component{

  constructor(){
    super();

    this.state={
      monsters: [] //Empty array of values as STATE, then we populate
    }
    console.log('constructor');
  }

  componentDidMount(){
    console.log('componentDidMount');
    fetch('https://jsonplaceholder.typicode.com/users')
    .then((response) => response.json())
    .then((users)=>
      this.setState(
        ()=>{return{monsters: users};},
        ()=>{console.log(this.state);}
      )
    )
  }

  render (){
    console.log('render');
    return(
    //-----------------------------------
      <div className="App">
        <input 
        className='search-box' 
        type='search' 
        placeholder='search'
        onChange={(event)=>{
        console.log(event);
        }}>
        </input>
        {this.state.monsters.map((monster)=>{
            return (
              <div key={monster.id}>
                <h1>{monster.name}</h1>
              </div>);
        })}
      </div>        
    //-----------------------------------
    );
  }
}

export default App
